import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { JobService } from '../../services/job.service';
import { ToastrService } from 'ngx-toastr'; // ✅ Toastr import added
import { Location } from '@angular/common';

@Component({
  selector: 'app-job-details',
  templateUrl: './job-details.component.html',
  styleUrls: ['./job-details.component.css'],
})
export class JobDetailsComponent implements OnInit {
  job: any = {};
  isLoading = true;
  errorMessage = '';
  userType: 'applicant' | 'poster' = 'applicant';

  constructor(
    private route: ActivatedRoute,
    private jobService: JobService,
    private router: Router,
    private toastr: ToastrService, // ✅ Toastr injected
    private location: Location
  ) {}
  

  ngOnInit(): void {
    const currentUrl = window.location.href;
    if (currentUrl.includes('/poster/')) {
      this.userType = 'poster';
    } else if (currentUrl.includes('/applicant/')) {
      this.userType = 'applicant';
    }

    const jobId = this.route.snapshot.paramMap.get('id');
    if (jobId) {
      this.fetchJobById(+jobId);
    }
  }

  fetchJobById(id: number): void {
    this.jobService.getJobById(id).subscribe(
      (data) => {
        this.job = data;
        this.isLoading = false;
      },
      (error) => {
        this.errorMessage = error.message || 'Failed to load job details.';
        this.isLoading = false;
        this.toastr.error(this.errorMessage, 'Error'); // ✅ Error toast
      }
    );
  }

  applyToJob(): void {
    const applicationData = { applicant: 'userId' }; // Replace with real user ID logic
    this.jobService.applyToJob(this.job.id, applicationData).subscribe(
      () => this.toastr.success('Applied successfully!', 'Success'), // ✅ Success toast
      () => this.toastr.error('Failed to apply.', 'Error') // ✅ Error toast
    );
  }

  deleteJob(jobId: number): void {
    if (confirm('Are you sure you want to delete this job?')) {
      this.jobService.deleteJob(jobId).subscribe(
        () => {
          this.toastr.success('Job deleted successfully.', 'Deleted'); // ✅ Success toast
          this.router.navigate(['/poster/jobs']);
        },
        (error) => {
          this.toastr.error('Failed to delete job.', 'Error'); // ✅ Error toast
          console.error('Delete error:', error);
        }
      );
    }
  }

  goBack(): void {
    this.location.back();
  }
}
<div class="container mt-4"> 
  <h2 class="section-title">Job Details</h2>

  <!-- Loading Spinner -->
  <div *ngIf="isLoading" class="text-center">
    <mat-spinner diameter="50"></mat-spinner>
  </div>

  <!-- Error Message -->
  <div *ngIf="errorMessage" class="alert alert-danger">
    {{ errorMessage }}
  </div>
<!-- Go Back Icon Button -->
<button mat-icon-button color="primary" (click)="goBack()" matTooltip="Go Back">
  <mat-icon>arrow_back</mat-icon>
</button>
  <!-- Job Details -->
  <div *ngIf="!isLoading && !errorMessage" class="job-details">
    <div class="job-detail">
      <p><strong>Job Title:</strong> {{ job.job_title }}</p>
      <p><strong>Company Name:</strong> {{ job.company_name }}</p>
      <p><strong>Location:</strong> {{ job.location }}</p>
      <p><strong>Job Type:</strong> {{ job.job_type }}</p>
      <p><strong>Salary Range:</strong> {{ job.salary_range || 'N/A' }}</p>
      <p><strong>Job Description:</strong> {{ job.job_description }}</p>
      <p><strong>Application Deadline:</strong> {{ job.application_deadline | date }}</p>
    </div>

    <!-- Action Buttons Section -->
    <div class="actions">
      

      <ng-container *ngIf="userType === 'poster'">
        <!-- Centered Edit and Delete Buttons -->
        <div class="edit-delete-buttons">
          <button mat-raised-button color="accent" [routerLink]="['/poster/jobs/edit', job.id]">
            Edit
          </button>
          <button mat-raised-button color="warn" (click)="deleteJob(job.id)">
            Delete
          </button>
        </div>
      </ng-container>
      <ng-container *ngIf="userType === 'applicant'">
        <button mat-stroked-button color="accent" [routerLink]="['/applicant/apply', job.id]" matTooltip="Apply for Job">
          Apply
        </button>
      </ng-container>
    </div>
  </div>
</div>
