import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}
<div class="home-container">
  <h1>Welcome to the Job Portal</h1>
  <p class="quote">"The best way to predict the future is to create it." - Abraham Lincoln</p>
  <div class="button-group">
    <button mat-raised-button color="primary" routerLink="/applicant">I'm an Applicant</button>
    <button mat-raised-button color="accent" routerLink="/poster">I'm a Job Poster</button>
  </div>
</div>
